import React from "react";
import SearchButton from "../SearchButton"


export default function TextBox(props) {
    return (
        <div className="row">
            <div className="col-sm-3">
                Search Book:

            <input className="form-control" type="text" name="search" value={props.search}  onChange={props.handleInputChange}/>
            <SearchButton />
            </div>

        </div>
    )
}
