import React from "react"

export default function ListItem(props) {
    return (
        <div className="list-group mt-5">
     { props.books.map(book => {
         return (
            <a href={props.volumeInfo.selfLink} className="list-group-item list-group-item-action">
                <div className="row">
                    <div className="col-sm-3">
                        <img src={props.volumeInfo.imageLink.thumbnail} />
                    </div>
                    <div className="col-sm-9 text-left">
                        <div className="d-flex w-100 justify-content-between">
                            <h5 className="mb-1">{props.volumeInfo.title}</h5>
                        </div>
                        <div className="d-flex w-100 justify-content-between">
                            <h5 className="mb-1">{props.volumeInfo.authors.join(" , ")}</h5>
                        </div>
                        <p className="mb-1">{props.volumeInfo.description}</p>
                        <button className="btn btn-primary">Save</button>
                    </div>
                </div>
            </a>
         )

     })}


        </div>
    )
}
